            o.buffer = gl.createBuffer();

            gl.bindBuffer( gl.ARRAY_BUFFER, o.buffer );

            gl.bufferData( gl.ARRAY_BUFFER, new Float32Array( o.data ), gl.STATIC_DRAW );

            o.numItems = o.data.length / o.itemSize;

