            vertices.push.apply(vertices, b);
            side.push( b );

            vertices.push.apply(vertices, center);
            side.push( center );

            vertices.push.apply(vertices, b);
            side.push( b );

            vertices.push.apply(vertices, c);
            side.push( c );

            vertices.push.apply(vertices, center);
            side.push( center );

            vertices.push.apply(vertices, c);
            side.push( c );

            vertices.push.apply(vertices, d);
            side.push( d );

            vertices.push.apply(vertices, center);
            side.push( center );

            vertices.push.apply(vertices, d);
            side.push( d );

            vertices.push.apply(vertices, e);
            side.push( e );

            vertices.push.apply(vertices, center);
            side.push( center );

            vertices.push.apply(vertices, e);
            side.push( e );

            vertices.push.apply(vertices, a);
            side.push( a );

            vertices.push.apply(vertices, center);
            side.push( center );

            sides.push( side );

