       let geo = prim.geometry;

        // Shortcuts to Prim data arrays

        let vertices = geo.vertices.data,
        indices  = geo.indices.data,
        texCoords = geo.texCoords.data,
        normals = geo.normals.data,
        tangents = geo.tangents.data;

        // Vertices.

        // Indices.

