        let pMatrix = arr[5];
        let mvMatrix = arr[6];
        let program = arr[7];
        let vsVars = arr[8];
        let fsVars = arr[9];

        // Attach objects.

        let shaderProgram = program.shaderProgram;

        window.vs2Vars = vsVars; /////////////////////////////////////////////////////////

