        //[gl, canvas, mat4, vec3, pMatrix, mvMatrix, program ] = this.setup();

        let arr = this.setup();
        let gl = arr[0];
        let canvas = arr[1];
        let mat4 = arr[2];
        let mat3 = arr[3];
        let vec3 = arr[4];
        let pMatrix = arr[5];
